import { makeAutoObservable } from "mobx";
import jwtDecode from "jwt-decode";

export default class UserStore {
    constructor() {
        this._isAuth = false;
        this._user = {};
        makeAutoObservable(this);
        this.checkValidToken = this.checkValidToken.bind(this);
    }

    checkValidToken = () => { // Изменяем метод на стрелочную функцию
        let isExpired = true; // По умолчанию считаем, что токен недействителен

        // Получаем токен из localStorage
        const token = localStorage.getItem('token');

        // Если токен существует, пытаемся его декодировать
        if (token) {
            const decodedToken = jwtDecode(token);
            const dateNow = new Date();

            // Проверяем, не истек ли срок действия токена
            if (decodedToken.exp > dateNow.getTime()) {
                isExpired = false;
            }
        }

        return isExpired;
    }

    setIsAuth(bool) {
        this._isAuth = bool;
    }

    setUser(user) {
        this._user = user;
    }

    get isAuth() {
        return this._isAuth;
    }

    get user() {
        return this._user;
    }
}
